import React, { useEffect, useState } from "react";

export default function App() {
  const [isLoading, setIsLoading] = useState(false); // Inicialmente, no estamos cargando
  const [imageUrl, setImageUrl] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (isLoading) {
      async function fetchData() {
        try {
          // Simulamos una carga más larga con setTimeout
          setTimeout(async () => {
            const response = await fetch("https://dog.ceo/api/breed/hound/images/random");
            if (response.ok) {
              const dog = await response.json();
              setImageUrl(dog.message);
              setError(null);
              setIsLoading(false);
            } else {
              setError("No hay imagen");
            }
          }, 2000); // Espera 2 segundos antes de completar la carga
        } catch (error) {
          setError("El API no está respondiendo");
        }
      }
      fetchData();
    }
  }, [isLoading]);

  const handleLoadImages = () => {
    setIsLoading(true);
  };

  return (
    <div>
      <button onClick={handleLoadImages}>Cargar imágenes de perros</button>
      {isLoading && <p>Cargando...</p>}
      {error && <p>{error}</p>}
      {imageUrl && <img src={imageUrl} alt="Perro" />}
    </div>
  );
}
